mapper.select:
      select 
  	t1.id as userOrgId, t1.enterprise_id as enterpriseId, t1.user_id as userId, t1.org_id as orgId, t1.org_name as orgName, 
  	t1.created as created, t1.updated as updated, t1.create_time as createTime, t1.update_time as updateTime, 
      t2.user_account as userAccount, t2.user_name as userName
      from user_org t1, user_info t2
      where t1.user_id = t2.id
      and t2.is_delete = 0 

          and t1.enterprise_id = #{enterpriseId,jdbcType=INTEGER}


          and t1.org_id = #{orgId,jdbcType=INTEGER}


			order by ${orderByClause}

mapper.update:
update user_org
set org_name = #{orgName,jdbcType=VARCHAR}
where org_id = #{orgId,jdbcType=INTEGER}
